const Skillz = () => {
    useEffect(() => {
        new WOW().init();
    }, []);

    const renderIcon = (skill) => {
        switch (skill.iconType) {
            case 'lineicon':
                return <i className={skill.icon} title={skill.title}></i>;
            case 'custom':
                return <img src={skill.icon} alt={skill.title} title={skill.title} />;
            case 'svg':
                return (
                    <span
                        className="svg-icon"
                        title={skill.title}
                        dangerouslySetInnerHTML={{ __html: skill.icon }}
                    ></span>
                );
            default:
                return null;
        }
    };

    return (
        <section id="Skills" className="skills section">
            <div className="container">
                <div className="row">
                    <div className="col-12">
                        <div className="section-title">
                        <h3 className="wow zoomIn" data-wow-delay=".2s">
    Skills
</h3>
<h2 className="wow fadeInUp" data-wow-delay=".4s">
    Continuously Evolving My Expertise
</h2>
<p className="wow fadeInUp" data-wow-delay=".6s">
    A versatile skill set and technological proficiency honed to create innovative and effective solutions.
</p>

                        </div>
                    </div>
                </div>
                <div className="row">
                    {categories.map((category) => (
                        <div
                            key={category.name}
                            className="col-lg-4 col-md-6 col-12"
                        >
                            <div
                                className="single-feature wow fadeInUp"
                                data-wow-delay=".2s"
                            >
                                <div className="skill-icons">
                                    {category.skills.map((skill, index) => (
                                        <div key={index}>
                                            {renderIcon(skill)}
                                        </div>
                                    ))}
                                </div>
                                <h3>{category.title}</h3>
                                <p>{category.description}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Skillz;
